import React, {useEffect, useState} from "react"
import { Link } from 'react-router-dom';
import { useParams} from "react-router-dom";
import axios from "axios";
import BasicExample from "./components/smth";

import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Articles() {
    const {id} = useParams()

    const [data, setData] = useState([])
    async function fetchSubjects() {
        const response = await axios.get('http://127.0.0.1:8000/api/articles/')
        console.log(response.data)
        setData(response.data)
    }

    useEffect( () => {
            console.log('effect')
            fetchSubjects()
        }, []
    )

    console.log(data)

    return (
        <div>
            <p><Link to="/">Главная</Link>/
                <Link to="/subjects">Предметы</Link>/
                {data.find(article => article.subject_id === +id)?.subject}
            </p>

            {data
                .filter(article => article.subject_id === +id)
                .map(article => {
                    return (
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src={article.photo_link} />
                            <Card.Body>
                                <Card.Title href="url">{article.link}>{article.link}</Card.Title>
                                <Card.Text>
                                    {article.title}
                                </Card.Text>
                                <Button variant="primary">Go somewhere</Button>
                            </Card.Body>
                        </Card>
                    )
                })}
        </div>
    )
}
